using System;
using BrawlStars.Logic;
using BrawlStars.Utilities.Netty;
using BrawlStars.Core;
using BrawlStars;

namespace BrawlStars.Protocol.Messages.Server
{
    public class OwnHomeDataMessage : PiranhaMessage
    {
        public OwnHomeDataMessage(Device device) : base(device)
        {
            Id = 24101;
            device.CurrentState = Device.State.Home;
            Device.LastVisitHome = DateTime.UtcNow;
        }

        public override void Encode()
        {
            Writer.WriteHex("a7caf601969609");
            Writer.WriteVInt(Resources.Trophies);
            Writer.WriteHex("");
            Writer.WriteScString(Resources.Name);
            Writer.WriteBoolean(true);
            Writer.WriteHex("ffffffff0827170001170401170801170c01171001171401171801171c01172001172401172801172c01173001173401173801173c01178001011784010117880101179f010117a4010117a9010117ae010117b3010117b8010117bd01011782020117b1020117b6020117bc02011782030117880301178e0301179a030117ac03010501");
            Writer.WriteVInt(99999);
            Writer.WriteVInt(5);
            Writer.WriteVInt(8);
            Writer.WriteVInt(Resources.Gold);
            Writer.WriteVInt(5);
            Writer.WriteVInt(9);
            Writer.WriteVInt(99999);
            Writer.WriteVInt(5);
            Writer.WriteVInt(10);
            Writer.WriteVInt(99999); //Tickets
            Writer.WriteHex("");
            Writer.WriteVInt(Resources.Gems);
            Writer.WriteHex("0000a4010000000000000002a8d2fec60bffff0000000000");
        }
    }
}
